import React, {Component} from 'react';
import './HomePage.css';

class HomePage extends Component{
    constructor(props){
        super(props);
        this.state = {
            searchValue : ""
        };

        this.searchMovie = this.searchMovie.bind();
    }


    render(){
        return(
            <div className="homepage">
                <div><span className="supra">SUPRA</span><span className="cine">CINE</span></div>
                <div>
                    <form className="search-field">
                        <input type="text" placeholder="Search.." value={this.searchValue}/>
                        <button type="submit" onClick={this.searchMovie}><i class="fa fa-search"></i></button>
                    </form>
                </div>
            </div>
        );
    }

    searchMovie() {
        var source = "http://www.omdbapi.com/?apikey=4eacbfee&" + this.searchValue;
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                this.setState({
                    isLoaded: true,
                    items: result.items
                });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
                }
            )
    }
}

export default HomePage;
