import React, {Component} from 'react';
import './HomePage.css';

class HomePage extends Component{
    constructor(props){
        super(props);
        this.state = {
            searchTitle: "hero",
            searchYear: 2020,
            searchType: "movie",
            typeOption:["movie", "series", "episode"],
            movieList: ""
        };

        //this.searchMovie = this.searchMovie.bind();
        this.changeValue = this.changeValue.bind();
    }


    componentDidMount() {
        var source = "http://www.omdbapi.com/?t=" + this.state.searchTitle + "&y=" + this.state.searchYear + "&type=" + this.state.searchType + "&apikey=4eacbfee";
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        movieList: result.items
                    });
                },
                (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
                }
            )
    }


    render(){
        return(
            <div className="homepage">
                <div><span className="supra">SUPRA</span><span className="cine">CINE</span></div>
                <div>
                    <form className="search-field">
                        <span>Title: </span><input type="text" name="searchTitle" value={this.state.searchTitle} onChange={this.changeValue}/>
                        <span>Year: </span><input type="text" name="searchYear" value={this.state.searchYear} onChange={this.changeValue}/>
                        <span>Type: </span>
                        <select>

                        </select>
                        <button type="submit" onClick={this.searchMovie}>Search</button>
                    </form>
                </div>
                <div>{this.state.movieList}</div>
            </div>
        );
    }

    changeValue(e){
        switch(e.target.name){
            case "searchTitle":
                this.setState({
                    searchTitle:e.target.value
                })
                break;
            case "searchYear":
                this.setState({
                    searchYear:e.target.value
                })
                break;
            default:
                break;

        }
    }
}

export default HomePage;
