import { Component } from "react";
import './SearchMovie.css';
import { withRouter } from "react-router-dom";

class MovieDetail extends Component{
    constructor(props){
        super(props);
        this.state = {
            movie : this.props.location.state.movie,
            movieInfo: ""
        }
    }

    componentDidMount(){
        var source = "http://www.omdbapi.com/?i=" + this.state.movie.imdbID + "&apikey=4eacbfee";
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                    if(result.Response === "False"){
                        this.setState({
                            movieInfo: ""
                        },()=>alert("Error! "+ result.Error));
                    }
                    this.setState({
                        movieInfo: result
                    });
                },
                (error) => {
                    this.setState({
                        movieInfo: ""
                    },()=>alert("Error!"));
                }
            );
    }

    render(){
        return(
            <div className="movie-detail">
                <div className="movie-image-div">
                    <img className="movie-img" src={this.state.movie.Poster} alt={this.state.movie.Title}></img>
                </div>
                <div className="movie-content">

                     <p>hello{this.state.movie.imdbID}</p>
                    <p>{"this.state.movie.Title"}</p>
                    <p>{"this.state.movie.Year"}</p>
                </div>
            </div>
        );
    }
}

export default withRouter(MovieDetail);
