import React, {Component} from 'react';
import MovieList from './MovieList';
import './SearchMovie.css';
import PageOption from './PageOption';
import { Route, Link, useHistory, withRouter, Switch, Redirect, BrowserRouter as Router } from 'react-router-dom';

class SearchMovie extends Component{

    constructor(props){
        super(props);
        this.state = {
            isLoaded: false,
            searchTitle: "hero",
            searchYear: 2020,
            searchType: "movie",
            movieList: [],
            totalResults: 0
        };

        this.searchMovie = this.searchMovie.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }


    componentDidMount() {
        this.searchMovie(1);
    }

    componentWillUnmount(){
    }


    render(){
        return(
            <Router>
                <div>  
                    <div className="search-field">
                        <span>Title: </span><input type="text" name="searchTitle" value={this.state.searchTitle} onChange={this.handleChange}/>
                        <span>Year: </span><input type="text" name="searchYear" value={this.state.searchYear} onChange={this.handleChange}/>
                        <span>Type: </span>
                        <select value={this.state.searchType} name="searchType" onChange={this.handleChange}>
                            <option value="movie">Movie</option>
                            <option value="series">Series</option>
                            <option value="episode">Episode</option>
                            <option value="">All</option>
                        </select>
                        <Link to={{pathname : `/${this.state.searchTitle}/${this.state.searchYear}/${this.state.searchType}`}}>
                            <button type="submit" onClick={this.searchMovie.bind(this,1)}>Search</button>
                        </Link>

                    </div>
                    <div className="movie-list">
                        <Switch>
                            <Route exact path="/">
                                <MovieList movieList={this.state.movieList}/>
                            </Route>
                            <Route path="/:t/:y/:type">
                                <MovieList movieList={this.state.movieList}/>
                            </Route>
                        </Switch>

                    </div>

                    <div className="clear" ></div> 
                    <div className="page-option">
                        <PageOption total={this.state.totalResults} selectPage={this.searchMovie}/>
                    </div>
                </div>
            </Router>
        );
    }

    searchMovie(page){
        var source = "http://www.omdbapi.com/?s=" + this.state.searchTitle + "&y=" + this.state.searchYear + "&type=" + this.state.searchType + "&page=" + page + "&apikey=4eacbfee";
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result.Search.length);
                    this.setState({
                        isLoaded: true,
                        movieList: result.Search,
                        totalResults: result.totalResults
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        movieList: [],
                        totalResults: 0
                    });
                }
            );

    }

    handleChange(e){
        let {name: fieldName, value} = e.target;

        this.setState({
            [fieldName]: value
        });
    }
}

export default SearchMovie;
