import React, {Component} from 'react';
import './HomePage.css';

class HomePage extends Component{

    constructor(props){
        super(props);
        this.state = {
            isLoaded: false,
            searchTitle: "hero",
            searchYear: 2020,
            searchType: "movie",
            movieList: ""
        };

        this.searchMovie = this.searchMovie.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }


    componentDidMount() {
        this.searchMovie();
    }


    render(){
        return(
            <div className="homepage">
                <div><span className="supra">SUPRA</span><span className="cine">CINE</span></div>
                <div className="search-field">
                    <span>Title: </span><input type="text" name="searchTitle" value={this.state.searchTitle} onChange={this.handleChange}/>
                    <span>Year: </span><input type="text" name="searchYear" value={this.state.searchYear} onChange={this.handleChange}/>
                    <span>Type: </span>
                    <select value={this.state.searchType} name="searchType" onChange={this.handleChange}>
                        <option value="movie">Movie</option>
                        <option value="series">Series</option>
                        <option value="episode">Episode</option>
                        <option value="">All</option>
                    </select>
                    <button type="submit" onClick={this.searchMovie}>Search</button>
                </div>
                <div>{this.state.movieList}</div>
            </div>
        );
    }

    searchMovie(){
        var source = "http://www.omdbapi.com/?s=" + this.state.searchTitle + "&y=" + this.state.searchYear + "&type=" + this.state.searchType + "&page=1&apikey=4eacbfee";
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        movieList: result.items
                    });
                },
                (error) => {
                this.setState({
                    isLoaded: true,
                    movieList: ""
                });
                }
            )
    }

    handleChange(e){
        let {name: fieldName, value} = e.target;

        this.setState({
            [fieldName]: value
        });
    }
}

export default HomePage;
