import React, {Component} from 'react';
import './HomePage.css';

class HomePage extends Component{
    constructor(props){
        super(props);
        this.state = {
            searchValue : "",
            searchTitle: "hero",
            searchYear: 2020,
            searchType: "movie",
            movieList: ""
        };

        //this.searchMovie = this.searchMovie.bind();
    }


    render(){
        return(
            <div className="homepage">
                <div><span className="supra">SUPRA</span><span className="cine">CINE</span></div>
                <div>
                    <form className="search-field">
                        <input type="text" placeholder="Search.." value={this.state.searchValue} readOnly/>
                        <button type="submit" onClick={this.searchMovie}><i className="fa fa-search"></i></button>
                    </form>
                </div>
                <div>{this.state.movieList}</div>
            </div>
        );
    }

    componentDidMount() {
        var source = "http://www.omdbapi.com/?t=" + this.state.searchTitle + "&y=" + this.state.searchYear + "&type=" + this.state.searchType + "&apikey=4eacbfee";
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        movieList: result.items
                    });
                },
                (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
                }
            )
    }
}

export default HomePage;
