import React, {Component} from 'react';
import MovieCard from './MovieCard';

class MovieList extends Component{
    constructor(props){
        super(props);
        this.state = {
            searchTitle: "hero",
            searchYear: 2020,
            searchType: "movie",
            movieList: [],
            totalResults: 0
        };

        this.searchMovie = this.searchMovie.bind(this);
    }

    componentDidMount() {
        this.searchMovie(1);
    }

    componentDidUpdate(){
        console.log(this.movieList);
    }

    render(){
        let movies = [];
        if(this.movieList){
            for(let i = 0; i < this.movieList.length; i++){
                movies.push(<MovieCard movie={this.movieList[i]} key={this.movieList[i].imdbID+i}/>);
            }
        }
        return <div className="movie-list">{movies}</div>;
    }

    searchMovie(page){
        var source = "http://www.omdbapi.com/?s=" + this.state.searchTitle + "&y=" + this.state.searchYear + "&type=" + this.state.searchType + "&page=" + page + "&apikey=4eacbfee";
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                    if(result.Response === "False"){
                        this.setState({
                            movieList: [],
                            totalResults: 0
                        },()=>alert("Error! "+ result.Error));
                    }
                    this.setState({
                        movieList: result.Search,
                        totalResults: result.totalResults
                    });
                },
                (error) => {
                    this.setState({
                        movieList: [],
                        totalResults: 0
                    },()=>alert("Error! "+ error));
                }
            );
    }
}

export default MovieList
