import { Component } from "react";
import './SearchMovie.css';
import { withRouter } from "react-router-dom";

class MovieDetail extends Component{
    constructor(props){
        super(props);
        this.state = {
            movie : this.props.location.state.movie,
            movieInfo: "",
            rate: "",
            rateStyle:{}
        }
    }

    componentDidMount(){
        var source = "http://www.omdbapi.com/?i=" + this.state.movie.imdbID + "&apikey=4eacbfee";
        fetch(source)
            .then(res => res.json())
            .then(
                (result) => {
                    if(result.Response === "False"){
                        this.setState({
                            movieInfo: ""
                        },()=>alert("Error! "+ result.Error));
                    }
                    let rate = "0%";
                    if(result.imdbRating !== "N/A")
                        rate = parseFloat(result.imdbRating)*10 + "%";
                    this.setState({
                        movieInfo: result,
                        rate: rate,
                        rateStyle: {
                            width: rate
                        }
                    });
                },
                (error) => {
                    this.setState({
                        movieInfo: ""
                    },()=>alert("Error!"));
                }
            );
    }


    filterText(t){
        if(t === "N/A")
            return ""
        else
            return t
    }

    render(){
        return(
            <div className="movie-detail">
                <div className="movie-image-div">
                    <img className="movie-img" src={this.state.movie.Poster} alt={this.state.movie.Title}></img>
                </div>
                <div className="movie-content">
                    <p className="movie-title">{this.state.movie.Title}</p>
                    <p className="left"><b>Rating : </b></p>
                    <div className="rate-div">
                        <div className="rate-container">
                            <div className="rate" style={this.state.rateStyle}>{this.state.rate}</div>
                        </div>
                    </div>
                    <div className="clear" ></div> 
                    <div className="info">
                        <p><b>Type : </b>{this.state.movie.Type}</p>
                        <p><b>Genre : </b>{this.state.movieInfo.Genre}</p>
                        <p><b>Year : </b>{this.state.movie.Year}</p>
                        <p><b>Released : </b>{this.state.movieInfo.Released}</p>
                        <p><b>Country : </b>{this.state.movieInfo.Country}</p>
                        <p><b>Language : </b>{this.state.movieInfo.Language}</p>
                        <p><b>RunTime : </b>{this.state.movieInfo.RunTime}</p>
                        <p><b>Director : </b> {this.state.movieInfo.Director}</p>
                        <p><b>Writer : </b> {this.state.movieInfo.Writer}</p>
                        <p><b>Actors : </b>  {this.state.movieInfo.Actors}</p>
                        <p className="highline"><b>Introduction : </b> {this.state.movieInfo.Plot}</p>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(MovieDetail);
